import java.util.Collections;import java.util.HashMap;import java.util.Map;import java.util.stream.Collectors;public class NTHSalaryMethod {    public static void main(String[] args) {        Map<String , Integer> map1 =new HashMap<>();        map1.put("Jay" , 50000);        map1.put("Ram",70000);        map1.put("Om",60000);        map1.put("Sham",30000);        map1.put("Raj",90000);        Map.Entry<String,Integer> result =getNTHSalary(2,map1);        System.out.println(result);    }    public static  Map.Entry<String,Integer> getNTHSalary(int num ,Map<String ,Integer> map1) {       return map1.entrySet().stream().sorted(Collections.reverseOrder(Map.Entry.comparingByValue())).collect(Collectors.toList()).get(num-1);    }}