import java.util.ArrayList;import java.util.Arrays;import java.util.Collections;public class Reverce {    public static void main(String[] args) {        String str = "I love Java";        String[] strArr = str.split(" ");        ArrayList<String> list = new ArrayList<>();        for (int i = strArr.length -1 ; i>=0 ; i--) {            list.add(strArr[i]);        }        list.forEach(e -> {            System.out.print(e+" ");        });    }}